CREATE TABLE `gestionfiat`.`turnos_registros` (`ID` INT(4) NOT NULL AUTO_INCREMENT , `REGISTRO` VARCHAR(100) NOT NULL , `TITULAR` VARCHAR(100) NOT NULL , `FECHA` DATE NOT NULL , `HORA` TIME NOT NULL , PRIMARY KEY (`ID`)) ENGINE = InnoDB;

CREATE TABLE `gestionfiat`.`usuarios` (`ID` INT(4) NOT NULL , `NICKS` VARCHAR(100) NOT NULL , `CONTRASENA` VARCHAR(100) NOT NULL , `NOMBRES` TEXT NOT NULL , `APELLIDOS` TEXT NOT NULL , `EDAD` INT(3) NOT NULL , `FECHA_ALTA` DATE on update CURRENT_TIMESTAMP NOT NULL ) ENGINE = InnoDB;